mapper.select:
select nvl(max(substr(employee_id, 14)), '00')
from employees_table
where substr(employee_id, 5, 8) = #{hireDate}

select nvl(max(substr(groupno, 12)), '00')
from document_table
where substr(groupno, 4, 8) = #{regDate}

select V.employee_seq, V.employee_name, V.employee_id, V.email, V.phone, V.hire_date, V.position_name, D.department_name, V.gender , V.birthday, V.address, V.postcode, V.detailaddress, V.extraaddress, V.grade from ( select E.employee_seq, E.employee_name, E.employee_id, E.employee_pw, E.email, E.phone, E.hire_date, E.fk_position, P.position_name, E.fk_department, E.gender , E.birthday, E.address, E.postcode, E.detailaddress, E.extraaddress, E.grade, E.fk_status from employees_table E join position_table P on E.fk_position = P.position_seq where E.employee_seq in
'${seqArr[i]}'
) V join department_table D on V.fk_department = D.department_seq order by V.fk_position asc, V.grade asc

select employee_seq
from employees_table
where fk_department = #{department_seq} and fk_position = #{position_seq} and employee_name = #{employee_name}

select employee_seq
from employees_table
where employee_id = #{employee_id}

mapper.update:
update employees_table set employee_pw = #{employee_pw}
						 , phone = #{phone}
						 , postcode = #{postcode}
						 , address = #{address}
						 , detailaddress = #{detailaddress}
						 , extraaddress = #{extraaddress}
where employee_id = #{employee_id}

mapper.select:
select fk_employee_seq
from attendance_table
where fk_employee_seq = #{fk_employee_seq} and to_char(onTime, 'yyyy. mm. dd') = #{onTime}

mapper.update:
update attendance_table set offTime = sysdate
where fk_employee_seq = #{fk_employee_seq}
	  and to_char(onTime, 'yyyymmdd') = to_char(sysdate, 'yyyymmdd')
