)My join (select Origin.mail_groupno, Origin.fk_employee_seq, E.employee_name, E.email, D.department_name, P.position_name from (select mail_groupno, max(fk_employee_seq) as fk_employee_seq from mail_table where mailStatus !=0 and (status = 2 and fk_employee_seq = #{loginSeq}) or (status in(0,1) and fk_employee_seq !=#{loginSeq}) group by mail_groupno ) Origin join employees_table E on Origin.fk_employee_seq = E.employee_seq join position_table P on E.fk_position = P.position_seq join department_table D on E.fk_department = D.department_seq where mail_groupno in (select mail_groupno from mail_table where mailStatus !=0 and fk_employee_seq = #{loginSeq}) )You on My.mail_groupno = You.mail_groupno) T where T.Ron between #{startRno} and #{endRno}
